sql.select:
select
    t1.*
from
    plugin_catalog t1
where
    1 = 1
    {{uuid | and:'t1.uuid'}}
    {{name | and:'t1.name'}}
    {{tag | and:'t1.tag'}}
    {{sort | orderBy}}
    {{offset | limit:pageSize}}

select
    count(1)
from
    plugin_catalog t1
where
    1 = 1
    {{uuid | and:'t1.uuid'}}
    {{name | and:'t1.name'}}
    {{tag | and:'t1.tag'}}
