require('colors');
module.exports = {
	error : function(data){
		console.log(this.getString(data).red);
	},
	warn : function(data){
		console.log(this.getString(data).yellow);
	},
	info : function(data){
		console.log(this.getString(data).blue);
	},
	ps : function(data,tag){
        tag = tag || '*';
        var i = 0;
        while(i<3){
            tag += tag+'';
            i++;
        }
		this.warn(tag);
		this.warn(data);
		this.warn(tag);
	},
	getString : function(data){
		if(data instanceof(Error)){
			data = data.message;
		}
		if(typeof data == "object"){
			try{
				data = JSON.stringify(data);
			}
			catch (e){
				data = e.message || '无法格式化输出对象';
			}
		}
		if(typeof data == "number"){
			data += "";
		}
		return data;
	}
}